<?phpfunction n(){    //echo '<br />';    echo '\n';}// open database$db_name = "sqlite:./myDB.sqlite";$db = new PDO($db_name);if ($db){     echo 'connect ok'; }else{     echo 'connect bad'; }n();$table_name = "performData".date("Ymd");echo $table_name;n();// translate value to json object$testDataObj = $_POST['testData'];$de_json = json_decode($testDataObj,TRUE);$createTableStr = "";$colIndex = 0;// use the json object property create the table colname$count_json = count($de_json);if ($count_json > 0){    foreach ($de_json[0] as $postKey => $value)    {        if ($colIndex > 0)        {            $createTableStr = $createTableStr.",";        }        $createTableStr = $createTableStr."$postKey"." varchar(255)";        $colIndex++;    }}$createTableStr = "CREATE TABLE $table_name ($createTableStr)";echo $createTableStr;n();//create table$db->exec($createTableStr);// insert the data$count_json = count($de_json);for ($i = 0; $i < $count_json; $i++){    $dt_record = $de_json[$i];    $tableField = "";    $tableValues = "";    $colIndex = 0;    foreach ($de_json[$i] as $postKey => $value)    {        if ($colIndex > 0)        {            $tableField = $tableField.",";            $tableValues = $tableValues.",";        }        $tableValues = $tableValues."'$value'";        $tableField = $tableField."'$postKey'";                $colIndex++;    }    $insertValueStr = "INSERT INTO $table_name ($tableField) values($tableValues)";    $db->exec($insertValueStr);}$db = null;n();echo 'run over'; ?>