<?phpfunction n(){    //echo '<br />';    echo '\n';}// open database$db_name = "sqlite:./myDB.sqlite";$db = new PDO($db_name);if ($db){     echo 'connect ok'; }else{     echo 'connect bad'; }n();$table_name = "performData";$base_table = "baseTable";echo $table_name;n();// translate value to json object$testDataObj = $_POST['testData'];$de_json = json_decode($testDataObj,TRUE);$createTableColStr = "";$baseTableColStr = "";$colIndex = 0;// use the json object property create the table colname$count_json = count($de_json);if ($count_json > 1){    foreach ($de_json[0] as $postKey => $value)    {        if ($colIndex > 0)        {            $baseTableColStr = $baseTableColStr.",";        }        $baseTableColStr = $baseTableColStr."$postKey"." varchar(255)";        $colIndex++;    }    $colIndex = 0;    foreach ($de_json[1] as $postKey => $value)    {        if ($colIndex > 0)        {            $createTableColStr = $createTableColStr.",";        }        $createTableColStr = $createTableColStr."$postKey"." varchar(255)";        $colIndex++;    }}$baseTableStr = "CREATE TABLE $base_table ($baseTableColStr)";//create table$db->exec($baseTableStr);$createTableStr = "CREATE TABLE $table_name ($createTableColStr)";//create table$db->exec($createTableStr);// insert base data$tableField = "";$tableValues = "";$colIndex = 0;foreach ($de_json[0] as $postKey => $value){    if ($colIndex > 0)    {        $tableField = $tableField.",";        $tableValues = $tableValues.",";    }    $tableValues = $tableValues."'$value'";    $tableField = $tableField."'$postKey'";        $colIndex++;}$insertValueStr = "INSERT INTO $base_table ($tableField) values($tableValues)";echo $insertValueStr;$db->exec($insertValueStr);    // insert the data$count_json = count($de_json);for ($i = 1; $i < $count_json; $i++){    $tableField = "";    $tableValues = "";    $colIndex = 0;    foreach ($de_json[$i] as $postKey => $value)    {        if ($colIndex > 0)        {            $tableField = $tableField.",";            $tableValues = $tableValues.",";        }        $tableValues = $tableValues."'$value'";        $tableField = $tableField."'$postKey'";                $colIndex++;    }    $insertValueStr = "INSERT INTO $table_name ($tableField) values($tableValues)";    $db->exec($insertValueStr);}$db = null;n();echo 'run over'; ?>